     /// </summary>
    public static partial class ReferenceLists
    {
        /// <summary>
        /// The complete <see cref="RefApipInteractionType"> List
         /// </summary>
        public static List<RefApipInteractionType> RefApipInteractionTypeList = new List<RefApipInteractionType> =
        {
            new RefApipInteractionType { Id=Guid.Parse("fe485d5f-ceb5-4ca4-b6ab-e172044ee987"), Code="The custom interaction provides an opportunity for extensibility of this specification to include support for interactions not currently documented.", Description="CustomInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("80d388e8-2cca-45c5-8fe3-c4ce5215c107"), Code="The drawing interaction allows the candidate to use a common set of drawing tools to modify a given graphical image (the canvas). It must be bound to a response variable with base-type file and single cardinality. The result is a file in the same format as the original image.", Description="DrawingInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("f1ac0876-f37d-4839-b3b8-885d0155a08c"), Code="A gap match interaction contains a number gaps that the candidate can fill from an associated set of choices. The candidate must be able to review the content with the gaps filled in context, as indicated by their choices.", Description="GapMatchInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("e3714109-db47-4bee-ab6c-bd8c484bcd93"), Code="A match interaction presents candidates with two sets of choices and allows them to create associates between pairs of choices in the two sets, but not between pairs of choices in the same set. Further restrictions can still be placed on the allowable associations using the matchMax attribute of the choices.", Description="MatchInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("db9b64b7-d661-42bb-a31f-28cb681cf691"), Code="A graphic gap-match interaction is a graphical interaction with a set of gaps that are defined as areas (hotspots) of the graphic image and an additional set of gap choices that are defined outside the image. The candidate must associate the gap choices with the gaps in the image and be able to review the image with the gaps filled in context, as indicated by their choices. Care should be taken when designing these interactions to ensure that the gaps in the image are a suitable size to receive the required gap choices. It must be clear to the candidate which hotspot each choice has been associated with. When associated, choices must appear wholly inside the gaps if at all possible and, where overlaps are required, should not hide each other completely. If the candidate indicates the association by positioning the choice over the gap (e.g., drag and drop) the system should 'snap' it to the nearest position that satisfies these requirements.", Description="GraphicGapMatchInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("a60a5afd-c6db-4d63-b9df-1f85dcdf987a"), Code="A hotspot interaction is a graphical interaction with a corresponding set of choices that are defined as areas of the graphic image. The candidate's task is to select one or more of the areas (hotspots). The hotspot interaction should only be used when the spatial relationship of the choices with respect to each other (as represented by the graphic image) is important to the needs of the item. Otherwise, choiceInteraction should be used instead with separate material for each option.", Description="HotspotInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("1d59be81-af6a-425e-95c3-9fac81f821e6"), Code="A graphic order interaction is a graphic interaction with a corresponding set of choices that are defined as areas of the graphic image. The candidate's task is to impose an ordering on the areas (hotspots). The order hotspot interaction should only be used when the spatial relationship of the choices with respect to each other (as represented by the graphic image) is important to the needs of the item. Otherwise, orderInteraction should be used instead with separate material for each option.", Description="GraphicOrderInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("2c6fa8c7-82ef-46d3-892d-1277e73de7a9"), Code="A graphic associate interaction is a graphic interaction with a corresponding set of choices that are defined as areas of the graphic image. The candidate's task is to associate the areas (hotspots) with each other. The graphic associate interaction should only be used when the graphical relationship of the choices with respect to each other (as represented by the graphic image) is important to the needs of the item. Otherwise, associateInteraction should be used instead with separate Material for each option.", Description="GraphicAssociateInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("908f52db-e17f-4187-8e08-8941b18c6fcd"), Code="", Description="8.00", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("a794204e-ec5a-4c00-8b12-8d2b813689f6"), Code="The choice interaction presents a set of choices to the candidate. The candidate's task is to select one or more of the choices, up to a maximum of maxChoices. The interaction is always initialized with no choices selected.", Description="ChoiceInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("c8d5ce60-7516-4131-b156-4d5f5c670e14"), Code="A inline choice is an inlineInteraction that presents the user with a set of choices, each of which is a simple piece of text. The candidate's task is to select one of the choices. Unlike the choiceInteraction, the delivery engine must allow the candidate to review their choice within the context of the surrounding text.", Description="InlineChoiceInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("4c6b9229-db9f-49ad-bc85-d1d492ec8f09"), Code="The media interaction allows more control over the way the candidate interacts with a time-based media object and allows the number of times the media object was experienced to be reported in the value of the associated response variable, which must be of base-type integer and single cardinality.", Description="MediaInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("81d417ce-728a-4bc8-9d01-be785fa70f2b"), Code="The hottext interaction presents a set of choices to the candidate represented as selectable runs of text embedded within a surrounding context, such as a simple passage of text. Like choiceInteraction, the candidate's task is to select one or more of the choices, up to a maximum of maxChoices. The interaction is initialized from the defaultValue of the associated response variable, a NULL value indicating that no choices are selected (the usual case).", Description="HottextInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("917d1f98-f12e-446a-94b4-43505c289f91"), Code="In an order interaction the candidate's task is to reorder the choices, the order in which the choices are displayed initially is significant. By default the candidate's task is to order all of the choices but a subset of the choices can be requested using the maxChoices and minChoices attributes. When specified the candidate must select a subset of the choices and impose an ordering on them.", Description="OrderInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("f6a78a2b-1825-4932-9d82-f7165c5b3c51"), Code="The position object interaction consists of a single image which must be positioned on another graphic image (the stage) by the candidate. Like selectPointInteraction, the associated response may have an areaMapping that scores the response on the basis of comparing it against predefined areas but the delivery engine must not indicate these areas of the stage. Only the actual position(s) selected by the candidate shall be indicated.", Description="PositionObjectInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("90cd6c99-a399-4dd8-8109-6365353b582e"), Code="A textEntry interaction obtains a simple piece of text from the candidate. Like inlineChoiceInteraction, the delivery engine must allow the candidate to review their choice within the context of the surrounding text.", Description="TextEntryInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("face2244-8197-4607-8691-be47349f1dc5"), Code="An extended text interaction allows the candidate to enter an extended amount of text.", Description="ExtendedTextInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("6af92de5-e103-4f61-a735-77bfd7d1180f"), Code="The end attempt interaction is a special type of interaction which allows item authors to provide the candidate with control over the way in which the candidate terminates an attempt. The candidate can use the interaction to terminate the attempt (triggering response processing) immediately, typically to request a hint. It must be bound to a response variable with base-type boolean and single cardinality.", Description="EndAttemptInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("cddb3291-7a14-4d82-a667-a097bbd60e71"), Code="The upload interaction allows the candidate to upload a pre-prepared file representing their response. It must be bound to a response variable with base-type file and single cardinality.", Description="UploadInteraction", Definition="", SortOrder=0 },
            new RefApipInteractionType { Id=Guid.Parse("0c6a6d9b-8d59-455d-8c44-08c5cf5ad448"), Code="An associate interaction presents candidates with a number of choices and allows them to create associations between them.", Description="AssociateInteraction", Definition="", SortOrder=0 },
        };

