            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("14b9b125-5a2b-41b7-8421-9483268bb9ff"), Code="Represents amounts to be amortized as debt premium/discount in connection with the issuance of bonds.", Description="194", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("bd081af4-dfd4-4741-99d5-36decb9ebf12"), Code="Current assets not provided for elsewhere.", Description="199", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("2ccb7106-2cb5-4a8c-a4bf-b749290d1981"), Code="Those assets that the school district intends to hold or continue to use over a long period of time. Specifically, capital assets include land, improvements to land, easements, buildings and building improvements, vehicles, machinery, equipment, works of art and historical treasures, infrastructure, and all other tangible or intangible assets that are used in operations and that have initial useful lives that extend beyond a single reporting period. This account is used only in proprietary funds, fiduciary funds, and the government-wide financial statements.", Description="200", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("a0f4a633-4996-402f-b220-d099c37a709a"), Code="A capital asset account that reflects the acquisition value of land owned by a school district. If land is purchased, this account includes the purchase price and costs such as legal fees, filling and excavation costs, and other associated improvement costs incurred to put the land in condition for its intended use. If land is acquired by gift, the account reflects its fair value at the time of acquisition. Permanent improvements to land, such as grading and fill, should also be accounted for in this account. Land and land improvements are considered nonexhaustible assets owing to their significantly long expected useful life. Nonexhaustible assets are not to be depreciated. Therefore, assets classified by asset code 211 should result in no depreciation expense.", Description="211", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("1316541f-f0db-4415-a4f3-e191e2874f97"), Code="A capital asset account that reflects the value of nonpermanent improvements to building sites, other than buildings, that add value to land. Examples of such improvements are fences, retaining walls, sidewalks, pavements, gutters, tunnels, and bridges. If the improvements are purchased or constructed, this account contains the purchase or contract price. If improvements are obtained by gift, it reflects the fair value at the time of acquisition. Site improvements are improvements that have a limited useful life. Because these improvements decrease in their value/usefulness over time, it is appropriate to depreciate these assets. Therefore, all capitalized site improvements should be depreciated over their expected useful life.", Description="221", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("482f10b0-f683-47a3-9311-17589c6ae76a"), Code="Accumulated amounts for the depreciation of land improvements.", Description="222", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("f52585e0-23e6-4bbc-860c-bfe0bdd0d8c9"), Code="A capital asset account that reflects the acquisition value of permanent structures used to house persons and property owned by the school district. If buildings are purchased or constructed, this account includes the purchase or contract price of all permanent buildings and the fixtures attached to and forming a permanent part of such buildings. This account includes all building improvements, including upgrades made to building wiring for technology. If buildings are acquired by gift, the account reflects their fair value at the time of acquisition.", Description="231", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("7ebefb90-3368-4535-8bb6-d93d22e7b80c"), Code="Accumulated amounts for the depreciation of buildings and building improvements.", Description="232", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("63182332-91f1-420f-9b1b-8c70978d72c0"), Code="Tangible property of a more or less permanent nature, other than land, buildings, or improvements thereto, that is useful in carrying on operations. Examples are machinery, tools, trucks, cars, buses, computers, purchased software, furniture, and furnishings. Appendix E provides criteria to distinguish whether a purchase is a supply or a piece of machinery or equipment.", Description="241", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("c592aac0-4e63-42c1-b3dc-e7a01936db03"), Code="Accumulated amounts for the depreciation of machinery and equipment.", Description="242", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("2dd29dff-165e-4c6d-8b61-246c949d0549"), Code="Individual items or collections of items that are of artistic or cultural importance.", Description="251", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("771bba2a-5e03-421b-942a-868f2c912879"), Code="Accumulated amounts for the depreciation (as applicable) of works of art and historical treasures.", Description="252", Definition="", SortOrder=0 },
