            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("dc42d154-d5ad-4868-99b0-37988ed8e2d4"), Code="Accumulated amounts for the depreciation of infrastructure assets.", Description="262", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("c4691980-18c9-47e4-b135-0fdea7e0e06d"), Code="The cost of construction work undertaken but not yet completed.", Description="271", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("71816787-f0b9-4517-a4a7-af067140d109"), Code="An intangible asset is a capital asset that lacks physical substance, is nonfinancial in nature, and has an initial useful life of more than 1 year. Intangible assets may be purchased or licensed, acquired through nonexchange transactions, or internally generated. Examples include easements, contractual rights, patents, trademarks, and computer software.", Description="281", Definition="", SortOrder=0 },
