            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("de2cd47f-dfbe-438f-a9a6-ff48d03e94ed"), Code="A liability account used to indicate amounts owed by a particular fund for services rendered. It is recommended that separate accounts be maintained for each interfund payable.", Description="402", Definition="", SortOrder=0 },
