            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("9c3f9126-3225-4201-879c-1f89ec4e272a"), Code="Other current liabilities not provided for elsewhere.", Description="499", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("75acc518-a22a-418b-a305-90f5483263a9"), Code="Obligations with a maturity of more than 1 year. These accounts should be used only with proprietary and fiduciary funds, as well as at the entity-wide level of reporting.", Description="500", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("845d1eb9-de77-4fde-b8ab-305b63762173"), Code="Bonds (general obligation, asset-backed, or revenue-backed) that have not reached or passed their maturity date and that are not due within 1 year.", Description="511", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("66557052-50ad-4270-9d77-1f041b7269f5"), Code="An account that represents interest that is accrued on deep discount bonds. This account should be used by school districts that issue capital appreciation bonds. Such bonds are usually issued at a deep discount from the face value, and no interest payment is made until maturity. Under full accrual accounting, the district is required to accrete the interest on the bonds over the life of the bonds. Accretion is the process of systematically increasing the carrying amount of the bond to its estimated value at the maturity date of the bond. To calculate accreted interest, the district should impute the effective interest rate, using the present value, the face value (or the future value), and the period of the bond, and multiply the effective interest rate by the book value of the debt at the end of the period. Accreted interest is usually recorded as an addition to the outstanding debt liability.", Description="512", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("1ff43c5e-ae7b-4da8-9547-b40de9f7d075"), Code="An account that represents the difference between the reacquisition price and the net carrying amount of old debt when a current or advance refunding of debt occurs. This account should be used only when defeasance of debt occurs for proprietary funds and entity-wide statements. The unamortized loss amount should be reported as a deferred outflow of resources and amortized as a component of interest expense in a systematic and rational manner over the remaining life of the old debt or the life of the new debt, whichever is shorter. The unamortized gain amount should be reported as a deferred inflow of resources and amortized as a component of interest expense in a systematic and rational manner over the remaining life of the old debt or the life of the new debt, whichever is shorter.", Description="513", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("a04b983c-3897-4654-a867-5cdefd1d3a27"), Code="An unconditional written promise signed by the maker to pay a certain sum of money 1 year or more after the issuance date.", Description="521", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("f5696dac-f79f-4dab-a9f8-d1459eb7cad1"), Code="Amounts remaining to be paid on capital lease agreements.", Description="531", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("d4b75e82-705c-4259-a461-0f90d514ed7c"), Code="Amounts remaining beyond the period of 1 year to be paid on compensated absences balances.", Description="551", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("687af3a4-1455-4956-8226-6c6a0b8f48f8"), Code="These are benefits offered for a short period of time to employees in connection with their termination of employment. Special termination benefits are often used as an inducement for early retirement or to address budgetary problems.", Description="553", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("fd5fadf0-6e82-4a3c-ad43-047b7daf0a33"), Code="Liabilities arising from arbitrage rebates to the Internal Revenue Service (IRS) from bond financing.", Description="561", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("bf839cd6-7b04-4763-8ef1-79d4c617babb"), Code="Other long-term liabilities not provided for elsewhere. This account represents amounts due after more than 1 year from the balance sheet date for advances from other funds and certain miscellaneous liabilities, including workers' compensation, self-funded insurance, special termination benefits, and legal claims and judgments.", Description="590", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("3f223d39-6c40-46fa-95e4-b14cf1d8c9e9"), Code="An acquisition of net assets by the government that is applicable to a future reporting period. A deferred inflow of resources should be recognized when resources are received or recognized as a receivable before (a) the period for which property taxes are levied or (b) the period when the resources are required to be used. When an asset is recorded in governmental fund financial statements but the revenue is not available, the government should report a deferred inflow of resources until such time as the revenue becomes available.", Description="600", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("7834ac36-1a85-451f-8d66-00da40d829e2"), Code="The nonspendable fund balance classification includes amounts that cannot be spent because they are either (a) not in spendable form or (b) legally or contractually required to be maintained intact. This would include items not expected to be converted to cash, including inventories and prepaid amounts. It may also include the long-term amount of loans and receivables, as well as property acquired for resale and the corpus (principal) of a permanent fund.", Description="710", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("ceddaa86-e7b2-4194-9e96-1f3bf6a76ae1"), Code="The restricted fund balance classification should be reported when legally enforceable constraints placed on the use of resources are either (a) externally imposed by creditors, grantors, contributors, or laws or regulations of other governments or (b) imposed by law through constitutional provisions or enabling legislation.", Description="720", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("a95efbd7-c133-45bc-b1b0-d084b5120849"), Code="The committed fund balance classification reflects specific purposes pursuant to constraints imposed by formal action at the district's highest level of decisionmaking authority (generally the governing board). Such constraints can only be removed or changed by the same form of formal action.", Description="730", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("8091dd3e-b7d1-4d4f-a444-450fbdeb412f"), Code="The assigned fund balance classification reflects amounts that are constrained by the government's intent to be used for specific purposes, but meet neither the restricted nor committed forms of constraint. Also, the assigned fund balance classification is the residual classification for the special revenue, debt service, capital projects, and/or permanent funds after nonspendable, restricted, and committed balances have been identified (unless the residual amount is negative, which would require presentation as unassigned fund balance).", Description="740", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("bb9d134e-b1ce-40e6-895f-2e402522664b"), Code="The unassigned fund balance classification is the residual classification, for the general fund only, after nonspendable, restricted, committed, and assigned balances have been identified. It is also used to report the residual amount for all other governmental funds after nonspendable, restricted, and committed balances have been identified, if the residual amount is negative.", Description="750", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("56fce5ef-1936-45cf-9ecc-3a1260381f9f"), Code="This account is used to record the component of net position invested in capital assets, net of related debt, that represents total capital assets less accumulated depreciation less debt directly related to capital assets. This account is to be used only in proprietary funds and entity-wide statements. (This was previously fund balance code 740.)", Description="760", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("b1c09530-0e76-46aa-876c-059f965701f5"), Code="This account is used to record the component of net position that represents net assets legally restricted by sources internal or external to the organization. This account is to be used only in proprietary funds and entity-wide statements. (This was previously fund balance code 750.)", Description="770", Definition="", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("9ccde9ec-1023-4f6a-867d-91d9d5c75e56"), Code="This account is used to record the component of net position that represents net position not classified in accounts 760 and 770. This account is to be used only in proprietary funds and entity-wide statements. (This was previously fund balance code 760.)", Description="780", Definition="", SortOrder=0 },
        };

