            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("15a4a85c-d377-4d98-8248-f7220bfc511a"), Code="All funds on deposit with a bank or savings and loan institution, normally in non-interest-bearing accounts. Interest-bearing accounts, however, that do not meet the definition of an investment (e.g., demand deposits that are interest-bearing) should be recorded herein as well.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("92609338-39db-4a69-82c3-c333a30277de"), Code="Currency, coins, checks, postal and express money orders, and bankers' drafts on hand.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("8741f369-41a2-46a8-94d3-30e3127917c5"), Code="A sum of money set aside to pay small obligations for which the issuance of a formal voucher and check would be too expensive and time consuming.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("35771fc1-6c31-46ee-93cf-f72e8876a04f"), Code="A sum of money set aside to provide change.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("7a17bd86-71b2-48b8-8c58-e1dc0488d5e7"), Code="Deposits with fiscal agents, such as commercial banks, for paying matured bonds and interest.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("d3142bd8-d0c3-4c8f-9a83-c40562d6e792"), Code="Securities and real estate held for producing income in the form of interest, dividends, rentals, or lease payments. Investments should be presented at fair value as of the reporting date. Gains from changes in the fair value of investments are recorded using revenue account 1530. Losses from changes in the fair value of investments are recorded using expenditure object code 930. Alternatively, gains and losses may be netted and recorded in revenue account 1530. The account does not include capital assets used in school district operations. Separate accounts may be maintained for each category of investments.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("c87184fb-e9f9-4ed7-8360-92093f71c14e"), Code="The excess of the amount paid for securities over the face value that has not yet been amortized. Use of this account is restricted to short- term money market investments.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("f73e5ec3-a488-467b-981b-ad0cd74484f9"), Code="The excess of the face value of securities over the amount paid for them that has not yet been written off. Use of this account is restricted to short-term investments.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("5e9778c0-f7a7-4f6c-8b7d-39da93198d54"), Code="The amount of interest receivable on investments, excluding interest purchased. Interest purchased should be shown in a separate account.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("8732f5d3-9653-4547-b2f7-e1d0d1a94fee"), Code="Interest accrued on investments between the last interest payment date and the date of purchase. The account is carried as an asset until the first interest payment date after the date of purchase.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("bb36ccab-f834-4a1b-8e91-7ee63d2903f0"), Code="The uncollected portion of taxes that a school district or government unit has levied and that has become due, including any interest or penalties that may be accrued. Separate accounts may be maintained on the basis of tax roll year, current and delinquent taxes, or both.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("9306b350-6490-4e50-b6af-17bedaaaf553"), Code="The portion of taxes receivable estimated not to be collected. The account is shown on the balance sheet as a deduction from the taxes receivable account to arrive at the net taxes receivable. Separate accounts may be maintained on the basis of tax roll year, delinquent taxes, or both.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("25cc93bd-a4f6-4ac2-8863-8bf22223371c"), Code="An asset account used to record a loan by one fund to another fund in the same governmental unit. It is recommended that separate accounts be maintained for each interfund receivable loan.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("e6d82d0c-0f31-4c18-a106-2f541d173e68"), Code="An asset account used to indicate amounts owed to a particular fund by another fund in the same school district for goods sold or services rendered. It is recommended that separate accounts be maintained for each interfund receivable.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("f7cd526c-9031-4fdb-ad07-eaeb86586c9f"), Code="Amounts due to the reporting governmental unit from another governmental unit. These amounts may represent grants-in-aid, shared taxes, taxes collected for the reporting unit by another unit, loans, and charges for services rendered by the reporting unit for another government. It is recommended that separate accounts be maintained for each interagency receivable.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("50fc86d5-b089-4570-9db7-43785fcc1b9c"), Code="Amounts that have been loaned to persons or organizations, including notes taken as security for such loans, where permitted by statutory authority.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("22c93b7d-dca7-4af7-a3bc-fa2e615fc1f4"), Code="The portion of loans receivable estimated not to be collected. The account is shown on the balance sheet as a deduction from the other loans receivable account.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("74803b66-3b0d-4fd0-8b02-3e330960f406"), Code="Amounts due on open account from private persons, firms, or corporations for goods and services furnished by a school district (but not including amounts due from other funds or from other governmental units).", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("f18fd9ec-107d-4020-8a69-160d361122ea"), Code="A provision for that portion of accounts receivable that is estimated will not be collected. The account is shown on the balance sheet as a deduction from the other accounts receivable account.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("9488cacb-9697-4865-83e7-6b5d91bbf8ab"), Code="The cost of supplies and equipment on hand not yet distributed to requisitioning units.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("73571fa4-11d5-4d24-825b-5bb10941534c"), Code="The value of goods purchased and held by a school district for resale rather than for use in its own operations. Such goods could include unique manufactured inventories, such as student-built homes or equipment.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("cb8c09c2-fc20-4b52-8771-7a809c30494e"), Code="Expenditures/expenses paid for benefits not yet received. Prepaid expenses differ from deferred charges in that they are spread over a shorter period of time and are regularly recurring costs of operation. Examples of prepaid expenses are prepaid rent, prepaid interest, and unexpired insurance premiums.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("df1b03c5-71f1-4951-b0b6-1c9145319b88"), Code="Funds deposited by the school district as a prerequisite to receiving services, goods, or both.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("0c6d8f9e-bcc5-4c33-af79-c186f6094ee9"), Code="Bond insurance costs that are a form of prepayment to be amortized.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("14b9b125-5a2b-41b7-8421-9483268bb9ff"), Code="Represents amounts to be amortized as debt premium/discount in connection with the issuance of bonds.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("bd081af4-dfd4-4741-99d5-36decb9ebf12"), Code="Current assets not provided for elsewhere.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("2ccb7106-2cb5-4a8c-a4bf-b749290d1981"), Code="Those assets that the school district intends to hold or continue to use over a long period of time. Specifically, capital assets include land, improvements to land, easements, buildings and building improvements, vehicles, machinery, equipment, works of art and historical treasures, infrastructure, and all other tangible or intangible assets that are used in operations and that have initial useful lives that extend beyond a single reporting period. This account is used only in proprietary funds, fiduciary funds, and the government-wide financial statements.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("a0f4a633-4996-402f-b220-d099c37a709a"), Code="A capital asset account that reflects the acquisition value of land owned by a school district. If land is purchased, this account includes the purchase price and costs such as legal fees, filling and excavation costs, and other associated improvement costs incurred to put the land in condition for its intended use. If land is acquired by gift, the account reflects its fair value at the time of acquisition. Permanent improvements to land, such as grading and fill, should also be accounted for in this account. Land and land improvements are considered nonexhaustible assets owing to their significantly long expected useful life. Nonexhaustible assets are not to be depreciated. Therefore, assets classified by asset code 211 should result in no depreciation expense.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("1316541f-f0db-4415-a4f3-e191e2874f97"), Code="A capital asset account that reflects the value of nonpermanent improvements to building sites, other than buildings, that add value to land. Examples of such improvements are fences, retaining walls, sidewalks, pavements, gutters, tunnels, and bridges. If the improvements are purchased or constructed, this account contains the purchase or contract price. If improvements are obtained by gift, it reflects the fair value at the time of acquisition. Site improvements are improvements that have a limited useful life. Because these improvements decrease in their value/usefulness over time, it is appropriate to depreciate these assets. Therefore, all capitalized site improvements should be depreciated over their expected useful life.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("482f10b0-f683-47a3-9311-17589c6ae76a"), Code="Accumulated amounts for the depreciation of land improvements.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("f52585e0-23e6-4bbc-860c-bfe0bdd0d8c9"), Code="A capital asset account that reflects the acquisition value of permanent structures used to house persons and property owned by the school district. If buildings are purchased or constructed, this account includes the purchase or contract price of all permanent buildings and the fixtures attached to and forming a permanent part of such buildings. This account includes all building improvements, including upgrades made to building wiring for technology. If buildings are acquired by gift, the account reflects their fair value at the time of acquisition.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("7ebefb90-3368-4535-8bb6-d93d22e7b80c"), Code="Accumulated amounts for the depreciation of buildings and building improvements.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("63182332-91f1-420f-9b1b-8c70978d72c0"), Code="Tangible property of a more or less permanent nature, other than land, buildings, or improvements thereto, that is useful in carrying on operations. Examples are machinery, tools, trucks, cars, buses, computers, purchased software, furniture, and furnishings. Appendix E provides criteria to distinguish whether a purchase is a supply or a piece of machinery or equipment.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("c592aac0-4e63-42c1-b3dc-e7a01936db03"), Code="Accumulated amounts for the depreciation of machinery and equipment.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("2dd29dff-165e-4c6d-8b61-246c949d0549"), Code="Individual items or collections of items that are of artistic or cultural importance.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("771bba2a-5e03-421b-942a-868f2c912879"), Code="Accumulated amounts for the depreciation (as applicable) of works of art and historical treasures.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("e73d2734-218b-4b77-9840-627e05de4690"), Code="A capital asset, network, or subsystem that has a useful life that is significantly longer than those of other capital assets. These assets may include water/sewer systems, roads, bridges, tunnels, and other similar assets.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("dc42d154-d5ad-4868-99b0-37988ed8e2d4"), Code="Accumulated amounts for the depreciation of infrastructure assets.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("c4691980-18c9-47e4-b135-0fdea7e0e06d"), Code="The cost of construction work undertaken but not yet completed.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("71816787-f0b9-4517-a4a7-af067140d109"), Code="An intangible asset is a capital asset that lacks physical substance, is nonfinancial in nature, and has an initial useful life of more than 1 year. Intangible assets may be purchased or licensed, acquired through nonexchange transactions, or internally generated. Examples include easements, contractual rights, patents, trademarks, and computer software.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("99a49206-f154-497c-b4e1-1b54342c5ca9"), Code="Accumulated amounts for the amortization of intangible assets.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("8d31b90c-0984-41de-b070-9e4b8ccd8f8d"), Code="A consumption of net assets by the government that is applicable to a future reporting period.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("a51acf82-880d-49b0-8247-81cf9568639e"), Code="A liability account used to record a debt owed by one fund to another fund in the same governmental unit. It is recommended that separate accounts be maintained for each interfund loan.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("de2cd47f-dfbe-438f-a9a6-ff48d03e94ed"), Code="A liability account used to indicate amounts owed by a particular fund for services rendered. It is recommended that separate accounts be maintained for each interfund payable.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("b3594bce-a77a-43f4-b9e8-cd629903ea40"), Code="Amounts owed by the reporting school district to another governmental unit. It is recommended that separate accounts be maintained for each intergovernmental payable.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("6178e789-0389-4357-9c37-228948930ca4"), Code="Liabilities on open account owed to private persons, firms, or corporations for goods and services received by a school district (but not including amounts due to other funds of the same school district or to other governmental units).", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("b716750a-ddfe-442a-bc5f-f3cf7cb5b618"), Code="Amounts due to be paid by a school district as the result of court decisions, including condemnation awards paid for private property taken for public use.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("8d5e5321-930f-45aa-8ebb-56cfba1e2466"), Code="Amounts due to designated payees in the form of a written order drawn by the school district directing the school district treasurer to pay a specific amount.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("af07b669-cce0-48c8-aea8-2f22b4728ed2"), Code="Amounts due on contracts for assets, goods, and services received by a school district.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("e0eacccd-4f00-466a-938a-e66d645d00ac"), Code="Liabilities on account of construction contracts for that portion of the work that has been completed but on which part the liability has not been paid pending final inspection, the lapse of a specified time period, or both. The unpaid amount is usually a stated percentage of the contract price.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("91f4c688-ef89-400d-b8b9-3df7143c80eb"), Code="Amounts due by a school district on contracts for constructing buildings and other structures and other improvements.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("56ad0f1a-5a54-4ca5-b6cf-56ff6b5c6e67"), Code="Bonds that have reached or passed their maturity date but that remain unpaid.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("9dfff4e2-475d-42f2-8438-66ec9f34a2fe"), Code="Bonds that have not reached or passed their maturity date but are due within 1 year or less. This account is used only in proprietary or fiduciary funds, as well as in the government-wide financial statements.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("97547ca9-b808-4f45-8a9f-e99101cc9c7a"), Code="An account that represents that portion of the excess of bond proceeds over par value and that remains to be amortized over the remaining life of such bonds. This account is used only in proprietary or fiduciary funds, as well as in the government-wide financial statements.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("38a38e43-de7d-468f-980f-61ca23085c74"), Code="Short-term obligations representing amounts borrowed for short periods of time, usually evidenced by notes payable or warrants payable.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("1558eec2-d609-459f-a3cf-a668c33d10b4"), Code="Capital lease obligations that are due within 1 year.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("73c14977-b127-49d2-921d-f10406c1ad65"), Code="Interest due within 1 year.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("ea718061-f80c-4908-b519-a31f72335689"), Code="Salary and fringe benefit costs incurred during the current accounting period that are not payable until a subsequent accounting period.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("adc141b2-9c14-45fe-b5bb-715d30959fa4"), Code="Amounts deducted from employees' salaries for withholding taxes and other purposes. District-paid benefits amounts payable are also included. A separate liability account may be used for each type of benefit.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("42eb3d04-1dc1-4663-82a5-f3d916231481"), Code="Compensated absences that will be paid within 1 year.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("9ad60022-bedc-4cf1-886c-010c53e8f979"), Code="A liability arising from payments not made to pension funds. This amount represents any difference between the actuarially determined annual required contribution and actual payments made to the pension fund.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("dba7ad82-ab4d-4284-a4bc-a4e58b51899a"), Code="A liability account that represents resources received from grantors before eligibility requirements are met.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("6736149b-702a-469c-b65d-dcf51e1b564c"), Code="Liability for deposits received as a prerequisite to providing or receiving services, goods, or both.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("9c3f9126-3225-4201-879c-1f89ec4e272a"), Code="Other current liabilities not provided for elsewhere.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("75acc518-a22a-418b-a305-90f5483263a9"), Code="Obligations with a maturity of more than 1 year. These accounts should be used only with proprietary and fiduciary funds, as well as at the entity-wide level of reporting.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("845d1eb9-de77-4fde-b8ab-305b63762173"), Code="Bonds (general obligation, asset-backed, or revenue-backed) that have not reached or passed their maturity date and that are not due within 1 year.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("66557052-50ad-4270-9d77-1f041b7269f5"), Code="An account that represents interest that is accrued on deep discount bonds. This account should be used by school districts that issue capital appreciation bonds. Such bonds are usually issued at a deep discount from the face value, and no interest payment is made until maturity. Under full accrual accounting, the district is required to accrete the interest on the bonds over the life of the bonds. Accretion is the process of systematically increasing the carrying amount of the bond to its estimated value at the maturity date of the bond. To calculate accreted interest, the district should impute the effective interest rate, using the present value, the face value (or the future value), and the period of the bond, and multiply the effective interest rate by the book value of the debt at the end of the period. Accreted interest is usually recorded as an addition to the outstanding debt liability.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("1ff43c5e-ae7b-4da8-9547-b40de9f7d075"), Code="An account that represents the difference between the reacquisition price and the net carrying amount of old debt when a current or advance refunding of debt occurs. This account should be used only when defeasance of debt occurs for proprietary funds and entity-wide statements. The unamortized loss amount should be reported as a deferred outflow of resources and amortized as a component of interest expense in a systematic and rational manner over the remaining life of the old debt or the life of the new debt, whichever is shorter. The unamortized gain amount should be reported as a deferred inflow of resources and amortized as a component of interest expense in a systematic and rational manner over the remaining life of the old debt or the life of the new debt, whichever is shorter.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("a04b983c-3897-4654-a867-5cdefd1d3a27"), Code="An unconditional written promise signed by the maker to pay a certain sum of money 1 year or more after the issuance date.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("f5696dac-f79f-4dab-a9f8-d1459eb7cad1"), Code="Amounts remaining to be paid on capital lease agreements.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("d4b75e82-705c-4259-a461-0f90d514ed7c"), Code="Amounts remaining beyond the period of 1 year to be paid on compensated absences balances.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("687af3a4-1455-4956-8226-6c6a0b8f48f8"), Code="These are benefits offered for a short period of time to employees in connection with their termination of employment. Special termination benefits are often used as an inducement for early retirement or to address budgetary problems.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("fd5fadf0-6e82-4a3c-ad43-047b7daf0a33"), Code="Liabilities arising from arbitrage rebates to the Internal Revenue Service (IRS) from bond financing.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("bf839cd6-7b04-4763-8ef1-79d4c617babb"), Code="Other long-term liabilities not provided for elsewhere. This account represents amounts due after more than 1 year from the balance sheet date for advances from other funds and certain miscellaneous liabilities, including workers' compensation, self-funded insurance, special termination benefits, and legal claims and judgments.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("3f223d39-6c40-46fa-95e4-b14cf1d8c9e9"), Code="An acquisition of net assets by the government that is applicable to a future reporting period. A deferred inflow of resources should be recognized when resources are received or recognized as a receivable before (a) the period for which property taxes are levied or (b) the period when the resources are required to be used. When an asset is recorded in governmental fund financial statements but the revenue is not available, the government should report a deferred inflow of resources until such time as the revenue becomes available.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("7834ac36-1a85-451f-8d66-00da40d829e2"), Code="The nonspendable fund balance classification includes amounts that cannot be spent because they are either (a) not in spendable form or (b) legally or contractually required to be maintained intact. This would include items not expected to be converted to cash, including inventories and prepaid amounts. It may also include the long-term amount of loans and receivables, as well as property acquired for resale and the corpus (principal) of a permanent fund.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("ceddaa86-e7b2-4194-9e96-1f3bf6a76ae1"), Code="The restricted fund balance classification should be reported when legally enforceable constraints placed on the use of resources are either (a) externally imposed by creditors, grantors, contributors, or laws or regulations of other governments or (b) imposed by law through constitutional provisions or enabling legislation.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("a95efbd7-c133-45bc-b1b0-d084b5120849"), Code="The committed fund balance classification reflects specific purposes pursuant to constraints imposed by formal action at the district's highest level of decisionmaking authority (generally the governing board). Such constraints can only be removed or changed by the same form of formal action.", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("8091dd3e-b7d1-4d4f-a444-450fbdeb412f"), Code="The assigned fund balance classification reflects amounts that are constrained by the government's intent to be used for specific purposes, but meet neither the restricted nor committed forms of constraint. Also, the assigned fund balance classification is the residual classification for the special revenue, debt service, capital projects, and/or permanent funds after nonspendable, restricted, and committed balances have been identified (unless the residual amount is negative, which would require presentation as unassigned fund balance).", SortOrder=0 },
            new RefFinancialAccountBalanceSheetCode { Id=Guid.Parse("bb9d134e-b1ce-40e6-895f-2e402522664b"), Code="The unassigned fund balance classification is the residual classification, for the general fund only, after nonspendable, restricted, committed, and assigned balances have been identified. It is also used to report the residual amount for all other governmental funds after nonspendable, restricted, and committed balances have been identified, if the residual amount is negative.", SortOrder=0 },
