        public static List<RefIDEADisabilityType> RefIDEADisabilityTypeList = new List<RefIDEADisabilityType> =
        {
            new RefIDEADisabilityType { Id=Guid.Parse("612b44f6-afc6-41fb-91bf-90ef41518f9e"), Code="Autism means a developmental disability significantly affecting verbal and nonverbal communication and social interaction, generally evident before age three, that adversely affects a child's educational performance. Other characteristics often associated with autism are engagement in repetitive activities and stereotyped movements, resistance to environmental change or change in daily routines, and unusual responses to sensory experiences.", Description="Autism", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("815fff37-f041-4fb5-b0ce-b36bcc02e9e0"), Code="Deaf-blindness means concomitant hearing and visual impairments, the combination of which causes such severe communication and other developmental and educational needs that they cannot be accommodated in special education programs solely for children with deafness or children with blindness.", Description="Deafblindness", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("242ff07b-29a8-4c5b-a298-aad548cdef95"), Code="Deafness means a hearing impairment that is so severe that the child is impaired in processing linguistic information through hearing, with or without amplification, that adversely affects a child's educational performance.", Description="Deafness", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("15a4d53a-7f24-4829-81e8-066563fad816"), Code="Developmental delays are defined by the State and as measured by appropriate diagnostic instruments and procedures in one or more of the following areas: Physical development, cognitive development, communication development, social or emotional development, or adaptive development; and result in the need of special education and related services.", Description="Developmentaldelay", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("b880767a-7274-49c6-9bf4-c984db1e518c"), Code="Emotional disturbance means a condition exhibiting one or more of the following characteristics over a long period of time and to a marked degree that adversely affects a child's educational performance: an inability to learn that cannot be explained by intellectual, sensory, or health factors; an inability to build or maintain satisfactory interpersonal relationships with peers and teachers; inappropriate types of behavior or feelings under normal circumstances; a general pervasive mood of unhappiness or depression, or tendency to develop physical symptoms or fears associated with personal or school problems.", Description="Emotionaldisturbance", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("bed2a386-ed77-4657-b128-7b5e0f1bb307"), Code="Hearing impairment means an impairment in hearing, whether permanent or fluctuating, that adversely affects a child's educational performance but that is not included under the definition of deafness.", Description="Hearingimpairment", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("18eb6cd8-fcf1-4a30-91e7-8bbe9e233585"), Code="Intellectual disability means significantly subaverage general intellectual functioning, existing concurrently with deficits in adaptive behavior and manifested during the developmental period, that adversely affects a child's educational performance.", Description="Intellectualdisability", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("9c1f0ca9-5d86-4d78-a27a-6e46a5f076fa"), Code="Multiple disabilities means concomitant impairments (such as intellectual disability-blindness or intellectual disability-orthopedic impairment), the combination of which causes such severe educational needs that they cannot be accommodated in special education programs solely for one of the impairments. Multiple disabilities does not include deaf-blindness.", Description="Multipledisabilities", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("39befdb4-47cf-4ada-88fc-c1c3db707a79"), Code="Orthopedic impairment means a severe orthopedic impairment that adversely affects a child's educational performance. The term includes impairments caused by a congenital anomaly, impairments caused by disease (e.g., poliomyelitis, bone tuberculosis), and impairments from other causes (e.g., cerebral palsy, amputations, and fractures or burns that cause contractures).", Description="Orthopedicimpairment", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("bf2d81c7-cadd-4308-b9a7-dcef622bfffb"), Code="Other health impairment means having limited strength, vitality, or alertness, including a heightened alertness to environmental stimuli, that results in limited alertness with respect to the educational environment, that is due to chronic or acute health problems such as asthma, attention deficit disorder or attention deficit hyperactivity disorder, diabetes, epilepsy, a heart condition, hemophilia, lead poisoning, leukemia, nephritis, rheumatic fever, sickle cell anemia, and Tourette syndrome; and diversely affects a child's educational performance.", Description="Otherhealthimpairment", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("27497c63-bcfd-45f2-8042-490fc67856a1"), Code="Specific learning disability means a disorder in one or more of the basic psychological processes involved in understanding or in using language, spoken or written, that may manifest itself in the imperfect ability to listen, think, speak, read, write, spell, or to do mathematical calculations, including conditions such as perceptual disabilities, brain injury, minimal brain dysfunction, dyslexia, and developmental aphasia.", Description="Specificlearningdisability", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("3881f27e-1cf8-4392-a00b-af80261b3771"), Code="Speech or language impairment means a communication disorder, such as stuttering, impaired articulation, a language impairment, or a voice impairment, that adversely affects a child's educational performance.", Description="Speechlanguageimpairment", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("fdad6c2e-126b-4cfb-ac92-2013dbc11113"), Code="Traumatic brain injury means an acquired injury to the brain caused by an external physical force, resulting in total or partial functional disability or psychosocial impairment, or both, that adversely affects a child's educational performance. Traumatic brain injury applies to open or closed head injuries resulting in impairments in one or more areas, such as cognition; language; memory; attention; reasoning; abstract thinking; judgment; problem-solving; sensory, perceptual, and motor abilities; psychosocial behavior; physical functions; information processing; and speech. Traumatic brain injury does not apply to brain injuries that are congenital or degenerative, or to brain injuries induced by birth trauma.", Description="Traumaticbraininjury", Definition="", SortOrder=0 },
            new RefIDEADisabilityType { Id=Guid.Parse("6f7cca61-a4b4-4dc0-9d43-8879f7cd1d19"), Code="Visual impairment including blindness means an impairment in vision that, even with correction, adversely affects a child's educational performance. The term includes both partial sight and blindness.", Description="Visualimpairment", Definition="", SortOrder=0 },
        };

