        {
            new RefIPEDSGASBFunctionalExpense { Id=Guid.Parse("bd2039d6-d744-4648-abed-f990f3e7507a"), Code="Instruction", Description="Instruction", Definition="A functional expense category that includes expenses of the colleges, schools, departments, and other instructional divisions of the institution and expenses for departmental research and public service that are not separately budgeted. Includes general academic instruction, occupational and vocational instruction, community education, preparatory and adult basic education, and regular, special, and extension sessions. Also includes expenses for both credit and non-credit activities. Excludes expenses for academic administration where the primary function is administration (e.g., academic deans). Information technology expenses related to instructional activities if the institution separately budgets and expenses information technology resources are included (otherwise these expenses are included in academic support). Institutions include actual or allocated costs for operation and maintenance of plant, interest, and depreciation.", SortOrder=Convert.ToDecimal("1.00") },
            new RefIPEDSGASBFunctionalExpense { Id=Guid.Parse("2c4e6728-ebe1-4058-a710-449b8f3e8c1a"), Code="Research", Description="Research", Definition="A functional expense category that includes expenses for activities specifically organized to produce research outcomes and commissioned by an agency either external to the institution or separately budgeted by an organizational unit within the institution. The category includes institutes and research centers, and individual and project research. This function does not include nonresearch sponsored programs (e.g., training programs). Also included are information technology expenses related to research activities if the institution separately budgets and expenses information technology resources (otherwise these expenses are included in academic support.) Institutions include actual or allocated costs for operation and maintenance of plant, interest, and depreciation.", SortOrder=Convert.ToDecimal("2.00") },
            new RefIPEDSGASBFunctionalExpense { Id=Guid.Parse("f64edb68-cd12-4d05-938c-01dd1eebc97b"), Code="Publicservice", Description="Public service", Definition="A functional expense category that includes expenses for activities established primarily to provide noninstructional services beneficial to individuals and groups external to the institution. Examples are conferences, institutes, general advisory service, reference bureaus, and similar services provided to particular sectors of the community. This function includes expenses for community services, cooperative extension services, and public broadcasting services. Also includes information technology expenses related to the public service activities if the institution separately budgets and expenses information technology resources (otherwise these expenses are included in academic support). Institutions include actual or allocated costs for operation and maintenance of plant, interest, and depreciation.", SortOrder=Convert.ToDecimal("3.00") },
            new RefIPEDSGASBFunctionalExpense { Id=Guid.Parse("3c2c0d2f-3449-4a2e-b376-fe3405b5d0f3"), Code="Academicsupport", Description="Academic support", Definition="A functional expense category that includes expenses of activities and services that support the institution's primary missions of instruction, research, and public service. It includes the retention, preservation, and display of educational materials (for example, libraries, museums, and galleries); organized activities that provide support services to the academic functions of the institution (such as a demonstration school associated with a college of education or veterinary and dental clinics if their primary purpose is to support the instructional program); media such as audiovisual services; academic administration (including academic deans but not department chairpersons); and formally organized and separately budgeted academic personnel development and course and curriculum development expenses. Also included are information technology expenses related to academic support activities; if an institution does not separately budget and expense information technology resources, the costs associated with the three primary programs will be applied to this function and the remainder to institutional support. Institutions include actual or allocated costs for operation and maintenance of plant, interest, and depreciation.", SortOrder=Convert.ToDecimal("4.00") },
            new RefIPEDSGASBFunctionalExpense { Id=Guid.Parse("e1004859-d04f-49da-b4b1-e8095f849bc2"), Code="Studentservices", Description="Student services", Definition="A functional expense category that includes expenses for admissions, registrar activities, and activities whose primary purpose is to contribute to students emotional and physical well-being and to their intellectual, cultural, and social development outside the context of the formal instructional program. Examples include student activities, cultural events, student newspapers, intramural athletics, student organizations, supplemental instruction outside the normal administration, and student records. Intercollegiate athletics and student health services may also be included except when operated as self-supporting auxiliary enterprises. Also may include information technology expenses related to student service activities if the institution separately budgets and expenses information technology resources(otherwise these expenses are included in institutional support.) Institutions include actual or allocated costs for operation and maintenance of plant, interest, and depreciation.", SortOrder=Convert.ToDecimal("5.00") },
            new RefIPEDSGASBFunctionalExpense { Id=Guid.Parse("948131da-0612-4996-860b-71911117a272"), Code="Institutionalsupport", Description="Institutional support", Definition="A functional expense category that includes expenses for the day-to-day operational support of the institution. Includes expenses for general administrative services, central executive-level activities concerned with management and long range planning, legal and fiscal operations, space management, employee personnel and records, logistical services such as purchasing and printing, and public relations and development. Also includes information technology expenses related to institutional support activities. If an institution does not separately budget and expense information technology resources, the IT costs associated with student services and operation and maintenance of plant will also be applied to this function.", SortOrder=Convert.ToDecimal("6.00") },
            new RefIPEDSGASBFunctionalExpense { Id=Guid.Parse("7f9f9ea0-96a1-4e3f-b8bc-6fa47c1c51fa"), Code="Auxiliaryenterprises", Description="Auxiliary enterprises", Definition="Expenses for essentially self-supporting operations of the institution that exist to furnish a service to students, faculty, or staff, and that charge a fee that is directly related to, although not necessarily equal to, the cost of the service. Examples are residence halls, food services, student health services, intercollegiate athletics (only if essentially self-supporting), college unions, college stores, faculty and staff parking, and faculty housing. Institutions include actual or allocated costs for operation and maintenance of plant, interest and depreciation.", SortOrder=Convert.ToDecimal("7.00") },
            new RefIPEDSGASBFunctionalExpense { Id=Guid.Parse("d78bf1f5-da8d-47e5-b817-c426da4775dc"), Code="Scholarshipsfellowships", Description="Scholarships and fellowships", Definition="That portion of scholarships and fellowships granted that exceeds the amount applied to institutional charges such as tuition and fees or room and board. The amount reported as expense excludes allowances and discounts. The FASB survey uses the term \u0022net grants in aid to students\u0022 rather than \u0022scholarships and fellowships.\u0022", SortOrder=Convert.ToDecimal("8.00") },
            new RefIPEDSGASBFunctionalExpense { Id=Guid.Parse("5d3a617b-bc19-41b0-bade-5fdfcac9f7e8"), Code="Hospitalservices", Description="Hospital services", Definition="Expenses associated with a hospital operated by the postsecondary institution (but not as a component unit) and reported as a part of the institution. This classification includes nursing expenses, other professional services, general services, administrative services, and fiscal services. Also included are information technology expenses, actual or allocated costs for operation and maintenance of plant, interest and depreciation related to hospital capital assets.", SortOrder=Convert.ToDecimal("9.00") },
            new RefIPEDSGASBFunctionalExpense { Id=Guid.Parse("7246557d-1436-4f87-9fa2-9cf53eb8c027"), Code="Independentoperations", Description="Independent operations", Definition="Expenses associated with operations that are independent of or unrelated to the primary missions of the institution (i.e., instruction, research, public service) although they may contribute indirectly to the enhancement of these programs. This category is generally limited to expenses of a major federally funded research and development center. Also includes information technology expenses, actual or allocated costs for operation and maintenance of plant, interest and depreciation related to the independent operations. Expenses of operations owned and managed as investments of the institution's endowment funds are excluded.", SortOrder=Convert.ToDecimal("10.00") },
            new RefIPEDSGASBFunctionalExpense { Id=Guid.Parse("3fa5ede6-8d16-4814-bcda-fbfaa3d14b76"), Code="Other", Description="Other", Definition="", SortOrder=Convert.ToDecimal("99.00") },
        };

