            new RefStudentSupportServiceType { Id=Guid.Parse("dfced3e0-420b-4236-af04-8c80f07a46f8"), Code="Programs designed to foster the development of the following physical activities: physical and motor fitness: fundamental motor skills and patterns; skills in aquatics and dance, including individual and group games; and sports (including intramural and lifetime sports). This term also includes special physical education, adapted physical education, movement education, and motor development.", Description="00290", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("2aaa8572-01c4-4582-af06-e3fecaf6f1d1"), Code="Programs that enable individuals who have mental, emotional, social, or physical problems to achieve self-expression and emotional release by communicating their emotions and conflicts graphically through painting, drawing, sculpting or other art forms. Art therapy can be used as diagnostic tool or a treatment technique.", Description="00291", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("78f61249-0b74-40cf-97a9-1a315517fa52"), Code="Programs that directly assist an individual with disabilities in the selection, acquisition, or use of an assistive technology device. Such assistance includes: 1) the evaluation of the needs of an individual, including a functional evaluation of the individual in his or her customary environment; 2) purchasing, leasing, or otherwise providing for the acquisition of assistive technology devices; 3) selecting, designing, fitting, customizing, adopting, applying, maintaining, repairing, or replacing assistive technology devices; 4) coordinating and using other therapies, interventions, or services (e.g., those associated with existing education and rehabilitation plans and programs); 5) training and technical assistance for an individual or, if appropriate, an individuals' family members; and 6) training or technical assistance for professionals (including those providing early intervention services), employers, or other individuals who are substantially involved.", Description="00292", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("5aeb7cd7-001e-4816-a090-f47ea9bc2a8a"), Code="Services that include: 1)identification of individuals with hearing loss: 2) determination of the range, nature, and degree of hearing loss, and communication functions, by use of audiological evaluation, and speech conservation; 4) creation and administration of programs for prevention of hearing loss; 5) counseling and guidance of students, parent/guardians, and teachers regarding hearing loss; and 6) determination of an individual's need for group and individual amplification, selecting and fitting an appropriate aid, and evaluating the effectiveness of amplification.", Description="00293", Definition="", SortOrder=0 },
