            new RefStudentSupportServiceType { Id=Guid.Parse("292b4e5d-403b-4041-910c-5e64dc346460"), Code="Programs that provide adaptive career and technical education training as a method of rehabilitating students with special needs, including those with disabilities. Program support continues beyond the rehabilitation and training phases, and includes job placement services.", Description="00889", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("d84d92e7-b848-48b3-873e-420e5aa4a512"), Code="Programs that develop case plans for the evaluation, treatment and/or care of individuals who, because of age, illness, disability or other difficulties, are unable to arrange for services on their own behalf. The services also assess the individual's needs, coordinate the delivery of services, ensure that services are obtained in accordance with the case plan, and monitor progress to ensure that services are having a desired impact.", Description="00878", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("32407028-f1c8-49ce-b989-ebe7fb16ece8"), Code="Programs that investigate reports of child abuse, neglect, and abandonment, and provide for the temporary placement of a child who, for his or her own protection, has been removed from custody of his or her parent/guardians. Program staff works with families that are experiencing the problem of child abuse with the objective of facilitating continued family unification or reunification. These programs also provide ongoing support services for children in permanent placement.", Description="00295", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("8e1d6831-f088-43c7-ae89-2100383c20f9"), Code="Programs that help participants develop or refine their communication skills, usually by focusing on communication in a particular context (e.g., helping a parent/guardian communicate with a child or helping professionals communicate with their clients). Training may emphasize active listening skills, problem-solving skills, conflict resolution techniques, body language, empathy, sensitivity to the values and/or cultural background of other, genuineness and other facets of the communication process.", Description="00881", Definition="", SortOrder=0 },
