            new RefStudentSupportServiceType { Id=Guid.Parse("30b5daa8-1780-4e7d-8e36-e4710149220b"), Code="Programs that provide equipment and facilities that ensure individuals with special needs can participate in specific recreational activities, games, or sports of their choice, regardless of age.", Description="00882", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("147b4bf1-76ad-4842-9ac7-dcbe81a57bfb"), Code="Curriculum planning is provided to the person or a group of persons.", Description="73048", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("69a72f0e-6a09-4406-b028-329dae68c143"), Code="An organized program that provides childcare services on a regular basis for a group of children; the program is directed toward the facilitation of several developmental areas.", Description="00334", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("b0d8f801-af70-4121-a48a-5f54c2e8e822"), Code="The design of learning environments and activities that promote the child's acquisition of skills in a variety of developmental areas, include cognitive processes and social interaction. ", Description="73047", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("c739b74e-eed8-472d-8c0e-80383bd01ffb"), Code="A program which provides services that: 1) are designed to meet the development needs of each child and family eligible, and are focused on enhancing the child's development; 2) are selected in collaboration with the parent/guardian; 3) are provided under public supervision by qualified personnel, in conformity with an individualized family service plan; 4) are offered at no cost unless subject to federal or state law mandating a system of payments (including a schedule of sliding fees); and 5) meet the standards of the state.", Description="00297", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("fd39a7b2-fc49-4ec2-8540-ea00843a5c18"), Code="Programs that provide diagnostic and treatment services which combine psychotherapeutic and tutorial techniques for individuals who have learning disabilities such as dyslexia, perceptual problems, emotional problems or other difficulties.", Description="00298", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("e662b04c-a901-4563-9296-db84665656a6"), Code="ESL/Migrant Service is provided to the person or a group of persons.", Description="73054", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("9c2badc3-7498-4cfc-8ad9-5ccad8aa5fbd"), Code="Programs that offer therapeutic sessions that focus on the system of relationships and communication patterns between family members and attempt to modify those relationships and patterns to achieve greater harmony. The therapist focuses on the family as a unit rather than concentrating on the member who has been singled out as being in need of treatment.", Description="00299", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("7fe3e547-5267-49ec-9e52-dd54fbf2c4a0"), Code="Services provided, as appropriate, by social workers, psychologists, and other qualified personnel to assist the family of a child eligible under this part in understanding the special needs of the child and enhancing the child's development.", Description="00333", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("131dd768-6753-4d7b-869d-11cda5250066"), Code="Programs whose primary purpose is to help individuals achieve and maintain physical well-being through the study, prevention, screening, evaluation and treatment of individuals who have illnesses, injuries, or disabilities.", Description="00303", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("14ea3d40-8176-4cec-9d46-894400b73411"), Code="Services provided to students in the home or in the hospital who are at home or who have been hospitalized.", Description="73060", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("a7b055ca-c017-4465-bd50-1e44bbb74200"), Code="Assistance provided to individuals with special needs including those with disabilities: 1) to learn the basic skills of daily living through individual and group counseling and instruction; 2) to experience and practice coping with real or simulated life situational demands; or 3) to use assistive devices, special equipment, or specialized assistant.", Description="00883", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("069da50b-577b-4564-92a3-d31bf1b20e7b"), Code="Career education and training provided to students in a personalized or individual setting.", Description="73058", Definition="", SortOrder=0 },
