            new RefStudentSupportServiceType { Id=Guid.Parse("211393d3-a70f-46f5-982f-277cef935977"), Code="Services provided by a licensed physician to determine a child's developmental status and need for early intervention services.", Description="00332", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("c46598ef-6da7-4b26-a717-f86ce6829570"), Code="Programs that specialize in the treatment of students who have adjustment or behavior problems, emotional disturbances, personality disorders, incipient illnesses, and/or other comparable conditions", Description="00305", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("b7517cb7-7713-44cd-909c-6a715bc9747c"), Code="Programs that provide services to individuals who have been hospitalized for treatment of acute or chronic mental or emotional disturbances, and facilitate their return to the community. The programs also offer psychiatric treatment in a setting that is less restrictive than inpatient hospitalization.", Description="00884", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("afab4a7c-bb71-428f-ac92-0b3aa01f4ddf"), Code="Programs that use music and music-related activities to help the student maintain mental functioning, to facilitate social and emotional growth, to promote communication, to develop constructive use of leisure time and to improve or maintain motor and perceptual skills. Music can be used as a passive agent as in the case of listening to music to aid in reminiscence, reality orientation or relaxation; or as an active creative process in which the client participates in musical production.", Description="00306", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("6f88a6a1-fa79-4cec-9f0a-2a3494e2bacc"), Code="A federally-funded program that provides supplemental nutrition in the form of a free meal, free snack, or free milk for income-eligible students who are unable to pay the cost. Public and non-profit private schools participating in the program are reimbursed for the meal/snack/milk served.", Description="00300", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("87519f79-7c9c-4dd0-bfc9-b61a9cd7a221"), Code="Programs that assist students who are unable to make their own record of classes or lectures.", Description="00308", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("bd5718c1-f91e-47ac-87da-e8b5ecedee02"), Code="Services that are provided for the assessment of health status for the purpose of providing nursing care, including the identification of patterns of human response to actual or potential health problems. They also include the provision of nursing care to prevent health problems, restore or improve functioning, and promote optimal health and development. Finally, they involve the administration of medications, treatments, and regimens prescribed by a licensed physician.", Description="00335", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("192ec808-9b33-4b3a-8360-7f631f5c39cd"), Code="Services that conduct individual assessments in nutritional history and dietary intake; anthropometrics, biochemical, and clinical variables; feeding skills and feeding problems; and food habits and food preferences. They also develop and monitor appropriate plans to address the nutritional needs of eligible children, and make referrals to appropriate community resources to carry out nutritional programs.", Description="00336", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("36c820a7-6a3b-4c09-a608-2378ca159d4a"), Code="Services that address the functional needs of an individual relating to adaptive development, adaptive behavior and play, and sensory, motor, and postural development. These services are designed to improve the individual's functional ability to perform tasks in the home, school, and community settings and include: 1) identification assessment and intervention; 2) adaptation of the environment, and selection, design and fabrication of assistive and orthodontic devices to facilitate the development and promote the acquisition of functional skills; and 3) prevention or minimization of the impact of initial or future impairment, delay in development, or loss of functional ability.", Description="00309", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("b1712041-cf60-4f99-96e5-fad74633a149"), Code="Programs that help individuals who are blind or who have visual impairments to develop the fundamental spatial concepts and skills that are necessary for maximum mobility and independent living. Instruction focuses on moving safely and efficiently in the school, home or community environment, and usually includes procedures for street crossings, travel in unfamiliar areas, utilization of public transportation, and appropriate use of aids such as sighted guides or canes.", Description="00310", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("2c102cb7-73a1-493f-be4d-3405517c1301"), Code="A student support service not yet defined in CEDS is provided to the person or a group of persons.", Description="09999", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("fd5f5210-6c15-4fa4-82b9-9a1994122712"), Code="Programs that offer classes, groups, or other educational opportunities for parents or potential parents who want to acquire the knowledge and skills to be effective in their parenting role.", Description="00311", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("144453a3-baaf-44be-8f20-867736761339"), Code="Programs that specialize in assisting the student through peer interaction. Such programs include peer counseling, tutoring, and leadership activities.", Description="00312", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("bc1c7de2-ebc7-4c98-8714-9ff76493bbec"), Code="Services to prevent or alleviate movement dysfunction and related functional problems that include: 1) screening, evaluation, and assessment of children and youth with disabilities to identify movement dysfunction; 2) obtaining, interpreting, and integrating information appropriate to program planning to prevent or alleviate movement dysfunction and related functional problems; and 3) providing individual or group services or treatment to prevent, alleviate, or compensate for movement dysfunction and related functional problems.", Description="00313", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("b6314ad7-db8b-4208-8dce-30fae33bef4e"), Code="Services that address the psychological needs of the individual through the administering of psychological needs of the individual through the administering of psychological and developmental tests, and other assessment procedures. They also involve the interpretation of assessment results. In addition, they include obtaining, integrating, and interpreting information about individual behavior, and individual and family conditions related to learning, mental health, and development. Finally, the services also involve the planning and management of a program of psychological services, including psychological counseling for children and parents, family counseling, consultation on child development, parent training, and education programs.", Description="00331", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("03094571-c0e5-4623-bc8c-e38cc22ce39b"), Code="Programs that provide textbook reading services for individuals who are blind or have visual impairments.", Description="00314", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("8f438e60-ad4a-4f70-a841-f733935d797c"), Code="Programs that may include: 1) assessment of leisure function; 2) therapeutic recreation services; 3) recreation programs in schools and community agencies; and 4) leisure education.", Description="00315", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("f6e2561d-b93f-45bd-94a5-7c7ca8fceed1"), Code="Programs that provide a combination of treatment and education services designed to restore maximum functioning , a sense of well-being, and a personally satisfying level of independence in individuals who have temporary or permanent disabilities.", Description="00318", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("fd7c1459-0158-40c2-9740-89e90e4da5de"), Code="Programs that provide a therapeutic living environment in community-based residential facilities for individuals with special needs, including those with emotional and/or behavioral problems. Such individuals require a structure, supervised treatment program that may include individual, group, family, and other treatment modalities as appropriate.", Description="00885", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("0a4dc7a3-d42a-4232-98dc-808343b9407d"), Code="Temporary childcare services that are short-term and non-medical in nature, provided either in or out of the home. These services are designed to provide temporary relief to the primary caregiver.", Description="73046", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("5bb9c2ce-b5ef-47c5-9576-7fa51e1cc42b"), Code="Programs that pay for or provide new or used clothing to students who need appropriate school attire and whose parent/guardians are unable to afford these items at retail prices.", Description="00319", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("b73d0c2b-af08-4e98-9a08-f3f370e135f4"), Code="Programs that provide advice and guidance for students who need assistance in choosing course or areas for major study, preparing for a vocation or further education, or coping with personal or learning problems.", Description="00302", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("3a26ffac-fcc5-4105-8092-59eae5935d08"), Code="Services provided by a qualified school health nurse or other registered nurse/nurse practitioner.", Description="00320", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("f24be3c5-7512-49e6-a9c8-69506a1846e1"), Code="Services provided by a school psychologist directly to the student.", Description="73061", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("ce9932ef-a891-49ad-8b1d-1d2581110c4c"), Code="Activities carried out by service coordinator to assist and enable an eligible student and his/her family to receive the rights, procedural safeguards, and services that are authorized to provided under the State's early intervention program. The specific activities include: coordinating the performance of evaluations and assessments; facilitating and anticipating in the development, review, and evaluation of individualized family service plans; assisting families in identifying available service providers; coordinating and monitoring the delivery of available services; informing families of the availability of advocacy services; coordinating with medical and health providers; and facilitating the development of a transition plan to preschool services, if appropriate.", Description="00294", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("82d30b44-6c2c-4668-9d3e-e4c8c84cb080"), Code="Services that involve making home visits to evaluate a child's living conditions and patterns of parent-child interaction; preparing a social or emotional developmental assessment of the child within the family context; providing individual and family-group counseling with parents and other family members, and appropriate social skill-building activities with the child and parents; working with those problems in a child's and family's living situation (home, community, and any center where early intervention services are provided) that affect the child's maximum utilization of early intervention services; and identifying, mobilizing, and coordinating community resources and services to enable the child and family to receive maximum benefit from early intervention services.", Description="00337", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("39f45f5e-15e8-46c9-8d14-ccc6e22774c3"), Code="Transportation arrangements provided to students to and from home, school, or other service sites.", Description="00321", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("86ba63c3-d98f-4938-af7b-b764d67d9fea"), Code="Individual or group therapy sessions that focus on the remediation of specific communicative or pharyngeal disorders and delays in the development of communication skills including: 1) speech sounds that are omitted, replaced by substitute sounds, or distorted; 2) voice problems in which pitch, loudness or quality of voice are affected; or 3) stuttering. Therapy includes the identification of an individual with such disorders and developmental delays, including the diagnosis and appraisal of specific disorders and delays in those skills. It also includes referral for medical or other professional services necessary for the habilitation or rehabilitation of individuals with those disorders and developmental delays. Finally, it includes the provision of services for habilitation, rehabilitation, or prevention of such disorder and developmental delays.", Description="00322", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("3d964849-ff55-4146-89a8-4ea1facfc5aa"), Code="Programs that provide individual or small group assistance for students who want to improve their study habits, test preparation skills and test-taking techniques.", Description="00323", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("be60400d-1b32-42f3-85f4-99ab35ac016c"), Code="Programs that provide a variety of services including: 1) individual, group and/or family counseling; 2) knowledge and skill-enhancing activities; and 3) structured groups that focus on family dynamics, problem solving, self-esteem, and/or similar issues. These programs care for students who are at risk of drug abuse (e.g., those who are currently in an experimental stage (light use), who have family members or peer groups who use alcohol or drugs, or who have behavioral, emotional, or other problems at home, in school, in the community, or with the criminal justice system). Substance abuse education programs also offer presentations about the risks of substance use to students at school and various other community settings.", Description="00324", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("0a5f2350-c8d8-4ac7-b5ee-1e37728ef3e8"), Code="Programs that find paid, meaningful work in a variety of integrated settings for individuals who have special needs, including those with developmental disabilities. Services include assigning a "job coach" to work side-by-side with each client, offering advocacy to the employer and other employees, training in basic job skills and work-related behaviors, assisting with specific tasks as needed, and providing initial and ongoing support as required to ensure that the individual retains competitive employment.", Description="00886", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("449f0161-48a7-4774-a203-6bd8bd22c6b5"), Code="Services provided by a staff member who specializes in helping teachers by providing in classroom strategies for working with students with an IDEA Disability Type of autism, providing professional development to local educators, and assisting in developing strategies to help parents work with their child at home.", Description="73062", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("07d1ea7f-8a24-4c92-8849-f25df17a2c0a"), Code="Services provided by a staff member who specializes in helping teachers by providing in classroom strategies for working with students with an IDEA Disability Type of emotional disturbance, providing professional development to local educators, and assisting in developing strategies to help parents work with their child at home.", Description="73064", Definition="", SortOrder=0 },
