            new RefStudentSupportServiceType { Id=Guid.Parse("1caebdc4-25d0-451f-9293-3e43b3d6ba58"), Code="Structural or sensory aids provided to students with special physical or physiological needs in order to help them to better adapt to everyday work and/or learning environments.", Description="00887", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("7d2e6274-cf82-4c9e-9106-eddac2d29d1b"), Code="Programs that offer family planning services including pregnancy counseling, birth control, and pregnancy testing which are especially designed to meet the needs and concerns of teenage students who are pregnant or are concerned about becoming pregnant or impregnating.", Description="00325", Definition="", SortOrder=0 },
