            new RefStudentSupportServiceType { Id=Guid.Parse("3961afce-9ac3-4ceb-9769-5ab1aa22bbf3"), Code="Services that allow individuals who can not comprehend or communicate using either the spoken or written word to understand or express themselves (e.g., foreign language translator and sign language interpreter).", Description="00327", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("e73dd83f-bdc8-4183-b53d-68af486abe39"), Code="Services that provide transportation for individuals, or make arrangements to transport individuals, to and from sites in which they are involved in vocational or life situations.", Description="00888", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("0a7ccad7-f407-4c26-ba2b-b1690c1da661"), Code="Individualized or small group instruction for students who need assistance in learning one or more components of a prescribed curriculum. Tutoring usually supplements a regular school program, and may be provided by peer students, teachers, or other personnel at school, in an individual's home, or at a community agency site.", Description="00329", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("45d7c96d-d93f-4fb5-aa33-1b376c614840"), Code="Services that include: 1) evaluation and assessment of visual functioning, including the diagnosis and appraisal of specific visual disorders, delays, and disabilities; 2) referrals for medical or other professional services necessary for the habilitation or rehabilitation of visual functioning disorders; and 3) training for communication, communication, orientation and mobility, visual, and independent living skills, and any additional training necessary to activate visual motor abilities.", Description="00330", Definition="", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("553de60e-893a-471c-a1d4-4a8868a58e10"), Code="Sustained interactions with industry or community professionals in real workplace settings, to the extent practicable, or simulated environments at an educational institution that foster in-depth, firsthand engagement with the tasks required in a given career field, that are aligned to curriculum and instruction.", Description="73055", Definition="", SortOrder=0 },
