        /// <summary>
        /// The RefStudentSupportServiceType Pick List
         /// </summary>
        public static List<RefStudentSupportServiceType> RefStudentSupportServiceTypePickList = new List<RefStudentSupportServiceType> =
        {
            new RefStudentSupportServiceType { Id=Guid.Parse("dfced3e0-420b-4236-af04-8c80f07a46f8"), Code="Programs designed to foster the development of the following physical activities: physical and motor fitness: fundamental motor skills and patterns; skills in aquatics and dance, including individual and group games; and sports (including intramural and lifetime sports). This term also includes special physical education, adapted physical education, movement education, and motor development.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("2aaa8572-01c4-4582-af06-e3fecaf6f1d1"), Code="Programs that enable individuals who have mental, emotional, social, or physical problems to achieve self-expression and emotional release by communicating their emotions and conflicts graphically through painting, drawing, sculpting or other art forms. Art therapy can be used as diagnostic tool or a treatment technique.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("78f61249-0b74-40cf-97a9-1a315517fa52"), Code="Programs that directly assist an individual with disabilities in the selection, acquisition, or use of an assistive technology device. Such assistance includes: 1) the evaluation of the needs of an individual, including a functional evaluation of the individual in his or her customary environment; 2) purchasing, leasing, or otherwise providing for the acquisition of assistive technology devices; 3) selecting, designing, fitting, customizing, adopting, applying, maintaining, repairing, or replacing assistive technology devices; 4) coordinating and using other therapies, interventions, or services (e.g., those associated with existing education and rehabilitation plans and programs); 5) training and technical assistance for an individual or, if appropriate, an individuals' family members; and 6) training or technical assistance for professionals (including those providing early intervention services), employers, or other individuals who are substantially involved.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("5aeb7cd7-001e-4816-a090-f47ea9bc2a8a"), Code="Services that include: 1)identification of individuals with hearing loss: 2) determination of the range, nature, and degree of hearing loss, and communication functions, by use of audiological evaluation, and speech conservation; 4) creation and administration of programs for prevention of hearing loss; 5) counseling and guidance of students, parent/guardians, and teachers regarding hearing loss; and 6) determination of an individual's need for group and individual amplification, selecting and fitting an appropriate aid, and evaluating the effectiveness of amplification.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("dbcbd4fb-21e5-49ff-819a-a6c1193535ad"), Code="Augmentative Communication Service is provided to the person or a group of persons.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("e381b2ff-ea79-47db-b4bf-2a0d93b10e11"), Code="Autism Spectrum Disorder Service is provided to the person or a group of persons.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("2a49439e-17d1-45c9-baf9-05c3eb658fe9"), Code="Behavior and Behavioral Consultation Service is provided to the person or a group of persons.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("4005ba4e-af54-4c3d-9eae-b3a3f2679901"), Code="Braille Service is provided to the person or a group of persons.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("292b4e5d-403b-4041-910c-5e64dc346460"), Code="Programs that provide adaptive career and technical education training as a method of rehabilitating students with special needs, including those with disabilities. Program support continues beyond the rehabilitation and training phases, and includes job placement services.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("d84d92e7-b848-48b3-873e-420e5aa4a512"), Code="Programs that develop case plans for the evaluation, treatment and/or care of individuals who, because of age, illness, disability or other difficulties, are unable to arrange for services on their own behalf. The services also assess the individual's needs, coordinate the delivery of services, ensure that services are obtained in accordance with the case plan, and monitor progress to ensure that services are having a desired impact.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("32407028-f1c8-49ce-b989-ebe7fb16ece8"), Code="Programs that investigate reports of child abuse, neglect, and abandonment, and provide for the temporary placement of a child who, for his or her own protection, has been removed from custody of his or her parent/guardians. Program staff works with families that are experiencing the problem of child abuse with the objective of facilitating continued family unification or reunification. These programs also provide ongoing support services for children in permanent placement.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("8e1d6831-f088-43c7-ae89-2100383c20f9"), Code="Programs that help participants develop or refine their communication skills, usually by focusing on communication in a particular context (e.g., helping a parent/guardian communicate with a child or helping professionals communicate with their clients). Training may emphasize active listening skills, problem-solving skills, conflict resolution techniques, body language, empathy, sensitivity to the values and/or cultural background of other, genuineness and other facets of the communication process.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("e33d7825-39bb-4bb0-858c-beb0bd9688ff"), Code="Career education and training provided to all students in community work settings.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("df6621b9-14c9-4fb1-bef5-21d402e79ce3"), Code="Career education and training provided specifically to students with disabilities in community work settings.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("30b5daa8-1780-4e7d-8e36-e4710149220b"), Code="Programs that provide equipment and facilities that ensure individuals with special needs can participate in specific recreational activities, games, or sports of their choice, regardless of age.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("147b4bf1-76ad-4842-9ac7-dcbe81a57bfb"), Code="Curriculum planning is provided to the person or a group of persons.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("69a72f0e-6a09-4406-b028-329dae68c143"), Code="An organized program that provides childcare services on a regular basis for a group of children; the program is directed toward the facilitation of several developmental areas.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("b0d8f801-af70-4121-a48a-5f54c2e8e822"), Code="The design of learning environments and activities that promote the child's acquisition of skills in a variety of developmental areas, include cognitive processes and social interaction. ", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("c739b74e-eed8-472d-8c0e-80383bd01ffb"), Code="A program which provides services that: 1) are designed to meet the development needs of each child and family eligible, and are focused on enhancing the child's development; 2) are selected in collaboration with the parent/guardian; 3) are provided under public supervision by qualified personnel, in conformity with an individualized family service plan; 4) are offered at no cost unless subject to federal or state law mandating a system of payments (including a schedule of sliding fees); and 5) meet the standards of the state.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("fd39a7b2-fc49-4ec2-8540-ea00843a5c18"), Code="Programs that provide diagnostic and treatment services which combine psychotherapeutic and tutorial techniques for individuals who have learning disabilities such as dyslexia, perceptual problems, emotional problems or other difficulties.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("e662b04c-a901-4563-9296-db84665656a6"), Code="ESL/Migrant Service is provided to the person or a group of persons.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("9c2badc3-7498-4cfc-8ad9-5ccad8aa5fbd"), Code="Programs that offer therapeutic sessions that focus on the system of relationships and communication patterns between family members and attempt to modify those relationships and patterns to achieve greater harmony. The therapist focuses on the family as a unit rather than concentrating on the member who has been singled out as being in need of treatment.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("7fe3e547-5267-49ec-9e52-dd54fbf2c4a0"), Code="Services provided, as appropriate, by social workers, psychologists, and other qualified personnel to assist the family of a child eligible under this part in understanding the special needs of the child and enhancing the child's development.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("131dd768-6753-4d7b-869d-11cda5250066"), Code="Programs whose primary purpose is to help individuals achieve and maintain physical well-being through the study, prevention, screening, evaluation and treatment of individuals who have illnesses, injuries, or disabilities.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("14ea3d40-8176-4cec-9d46-894400b73411"), Code="Services provided to students in the home or in the hospital who are at home or who have been hospitalized.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("a7b055ca-c017-4465-bd50-1e44bbb74200"), Code="Assistance provided to individuals with special needs including those with disabilities: 1) to learn the basic skills of daily living through individual and group counseling and instruction; 2) to experience and practice coping with real or simulated life situational demands; or 3) to use assistive devices, special equipment, or specialized assistant.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("069da50b-577b-4564-92a3-d31bf1b20e7b"), Code="Career education and training provided to students in a personalized or individual setting.", SortOrder=0 },
            new RefStudentSupportServiceType { Id=Guid.Parse("628bc16d-8199-45e5-a71c-c883ec4c28b5"), Code="Career education provided specifically to students with disabilities in a personalized or individual setting.", SortOrder=0 },
